namespace Schema.NET
{
    using System;
    using System.Runtime.Serialization;
    using Newtonsoft.Json;

    /// <summary>
    /// A Report generated by governmental or non-governmental organization.
    /// </summary>
    public partial interface IReport : IArticle
    {
        /// <summary>
        /// The number or other unique designator assigned to a Report by the publishing organization.
        /// </summary>
        OneOrMany<string> ReportNumber { get; set; }
    }

    /// <summary>
    /// A Report generated by governmental or non-governmental organization.
    /// </summary>
    [DataContract]
    public partial class Report : Article, IReport, IEquatable<Report>
    {
        /// <summary>
        /// Gets the name of the type as specified by schema.org.
        /// </summary>
        [DataMember(Name = "@type", Order = 1)]
        public override string Type => "Report";

        /// <summary>
        /// The number or other unique designator assigned to a Report by the publishing organization.
        /// </summary>
        [DataMember(Name = "reportNumber", Order = 306)]
        [JsonConverter(typeof(ValuesJsonConverter))]
        public OneOrMany<string> ReportNumber { get; set; }

        /// <inheritdoc/>
        public bool Equals(Report other)
        {
            if (other is null)
            {
                return false;
            }

            if (ReferenceEquals(this, other))
            {
                return true;
            }

            return this.Type == other.Type &&
                this.ReportNumber == other.ReportNumber &&
                base.Equals(other);
        }

        /// <inheritdoc/>
        public override bool Equals(object obj) => this.Equals(obj as Report);

        /// <inheritdoc/>
        public override int GetHashCode() => HashCode.Of(this.Type)
            .And(this.ReportNumber)
            .And(base.GetHashCode());
    }
}
